def get_function_timer(baza):
    date = datetime.now(pytz.timezone('Europe/Moscow'))
    t = str(date.time()).split(":")
    d = str(date.date()).split("-")
    time = f"{d[2]}-{d[1]}-{d[0]} в {t[0]}:{t[1]}"
    baza.update(dict(timer=time))
    db.session.commit()


def function_age(email):
    time = str(datetime.now(pytz.timezone('Europe/Moscow')).date()).split("-")
    age, birthday, birthday2 = "", "", ""
    try:
        for i in Baza.query.filter_by(email=email):
            age = i.age
            birthday = i.birthday
            birthday2 = i.birthday2
            break
    except:
        pass
    if age != "Неизвестно":
        k = age.split("(")[2].split('-')
        s = f"{int(age.split('л')[0])} л{age.split('л')[1]}"
        if int(k[0]) == int(time[2]) and int(k[1]) == int(time[1]) and s not in birthday:
            if f"{int(age.split('л')[0]) + 1} л{age.split('л')[1]}" not in birthday:
                birthday.append(f"{int(age.split('л')[0]) + 1} л{age.split('л')[1]}")
            Baza.query.filter_by(email=email).update(
                dict(age=f"{int(age.split('л')[0]) + 1} л{age.split('л')[1]}", birthday=birthday, birthday2="да"))
            db.session.commit()
        else:
            if birthday2 != "нет" and int(k[0]) != int(time[2]) and int(k[1]) == int(time[1]) or int(k[0]) == int(
                    time[2]) and int(k[1]) != int(time[1]) or int(k[0]) != int(time[2]) and int(k[1]) != int(time[1]):
                Baza.query.filter_by(email=email).update(dict(birthday2="нет"))
                db.session.commit()


