@app.route(
    "/account/a4a65cd1e8c0033fabc8ea5ec80070b6" + '<string:email>' + "/2ff4f516c5061" + "<string:code>" + "7be5c429ed35084347a",
    methods=["POST", "GET"])
def account(email, code):
    global account1
    count, baza, currency = 0, "", ""
    if email != "mf4sk7l":
        baza = Baza.query.filter_by(email=email)
        get_function_timer(baza)
        function_age(email)
    if email not in account1 and email != "mf4sk7l" and code == "0":
        account1[email] = [0, datetime.now(pytz.timezone('Europe/Moscow'))]
    if email != "mf4sk7l":
        try:
            for i in Baza.query.filter_by(id=1):
                currency = i.currency
                break
        except:
            pass
        rates = ExchangeRates(str(datetime.now().date()))
        if str(rates['USD'].rate) != currency[0]:
            if float(currency[0]) > rates['USD'].rate:
                currency[1] = "вниз"
            else:
                currency[1] = "вверх"
            currency[0] = str(rates['USD'].rate)
            Baza.query.filter_by(id=1).update(dict(currency=currency))
            db.session.commit()
        if str(rates['EUR'].rate) != currency[2]:
            if float(currency[2]) > rates['EUR'].rate:
                currency[3] = "вниз"
            else:
                currency[3] = "вверх"
            currency[2] = str(rates['EUR'].rate)
            Baza.query.filter_by(id=1).update(dict(currency=currency))
            db.session.commit()
    if request.method == "POST" and email != "mf4sk7l":
        if code == "0":
            account1[email][0] += 1
            my_vopros, questions = "", ""
        text = ""
        vopros = request.form["vopros"]
        if vopros == "":
            flash("Напишите суть вопроса", category="error")
        elif len(vopros) < 10:
            flash("Слов в суть вопроса мало", category="error")
        elif len(vopros) > 280:
            flash("Напишите меньше слов", category="error")
        elif len(request.form["text"]) < 10 and request.form["text"] != "":
            flash("Слов к дополнению вопроса мало", category="error")
        elif len(request.form["text"]) > 4000:
            flash("Слов в дополнение слишком много", category="error")
        else:
            if code == "0" and account1[email][0] < 5:
                for i in baza:
                    my_vopros = i.vopros
                    questions = i.questions + 1
                if len(request.form["text"]) > 300:
                    for i in range(300):
                        text += request.form["text"][i]
                    text += " ..."
                else:
                    text = request.form["text"]
                if vopros[-1] != "?":
                    vopros += " ?"
                for i in Baza.query.filter_by(id=1):
                    count = i.count
                time = datetime.now(pytz.timezone('Europe/Moscow'))
                t = str(time.time()).split(":")
                d = str(time.date()).split("-")
                date = f"{d[2]}-{d[1]}-{d[0]} в {t[0]}:{t[1]}"
                count += 1
                my_vopros.append([str(count), date, vopros, text, request.form["text"], email])
                baza.update(dict(vopros=my_vopros, questions=questions))
                Baza.query.filter_by(id=1).update(dict(count=count))
                db.session.commit()
                flash("Вопрос отправлен", category="success")
            elif code == "0":
                flash("Вы много раз задали вопрос, поэтому вопрос можно задать только через несколько минут",
                      category="error")
                if datetime.now(pytz.timezone('Europe/Moscow')).minute - account1[email][1].minute > 5:
                    account1[email] = [0, datetime.now(pytz.timezone('Europe/Moscow'))]
            else:
                vopros1 = ""
                if len(request.form["text"]) > 300:
                    for i in range(300):
                        text += request.form["text"][i]
                    text += " ..."
                else:
                    text = request.form["text"]
                if vopros[-1] != "?":
                    vopros += " ?"
                try:
                    for i in baza:
                        vopros1 = i.vopros
                        break
                except:
                    pass
                try:
                    for date in vopros1:
                        if code == date[0]:
                            date[2] = vopros
                            date[3] = text
                            date[4] = request.form["text"]
                            break
                except:
                    pass
                baza.update(dict(vopros=vopros1))
                flash("Вопрос исправлен", category="success")
                db.session.commit()
    flash("")
    return render_template("account.html", data=Baza.query.filter_by(email=email), email=email, code=code,
                           currency=currency)


