    count = 0
    for i in baza2:
        count = i.count
    random_num, spisock_voprosov = [], []
    if count > 5:
        while len(random_num) < 5:
            r = random.randint(1, count)
            if r not in random_num and code != str(r):
                random_num.append(str(r))
        random_num.sort()
    elif count <= 5:
        random_num = [str(i) for i in range(1, count + 1) if code != str(i)]
    for date in baza:
        for i in date.vopros:
            if i[0] in random_num:
                spisock_voprosov.append([i[1], i[2], i[3], i[5], i[0]])
